import React from 'react'
import {CategoryScale, Chart as ChartJS, Legend, LineElement, LinearScale, PointElement, Title, Tooltip} from 'chart.js'
import { Line } from 'react-chartjs-2'

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  )

export default function ScoreChart({scores}: {scores: {[pseudo: string]: number[]}}){
    console.log(scores)
    const data = {
        labels: Object.keys(scores[Object.keys(scores)[0]]), // Utilise les clés du premier joueur comme étiquettes
        datasets: Object.keys(scores).map((player, index) => ({
            label: `Joueur ${player}`,
            data: Object.values(scores[player]), // Utilise les valeurs de chaque joueur comme données
            backgroundColor: `rgba(${index * 50}, ${index * 100}, ${index * 150}, 0.5)`, // Couleur de fond pour chaque joueur
            borderColor: `rgba(${index * 50}, ${index * 100}, ${index * 150}, 1)`, // Couleur de bordure pour chaque joueur
            borderWidth: 1,
            fill: false,
        })),
    }

    // Options de la chart
    const options = {
        responsive: true,
        scales: {
            y: {
                beginAtZero: true,
            },
        },
    }

    return (
        <Line data={data} options={options} />
    )
  }
