import React, { useState } from 'react'
import {CategoryScale, Chart as ChartJS, Legend, LineElement, LinearScale, PointElement, Title, Tooltip} from 'chart.js'
import { Line } from 'react-chartjs-2'
import { FormControlLabel, Switch } from '@mui/material'

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
)

const cumulativeSum = (sum => value => sum += value)(0)

export default function ScoreChart({scores, pseudo, isCreator}: {scores: {[pseudo: string]: number[]}, pseudo: string, isCreator: boolean}){
    const [cummulative, setCummulative] = useState(false)

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setCummulative(event.target.checked)
    }

    const data = {
        labels: Object.keys(scores[Object.keys(scores)[0]]), // Utilise les clés du premier joueur comme étiquettes
        datasets: Object.keys(scores).map((player, index) => ({
            label: player,
            data: cummulative ? [0].concat(Object.values(scores[player]).map(cumulativeSum)) : Object.values(scores[player]),
            backgroundColor: `rgba(${index * 50}, ${index * 100}, ${index * 150}, 0.5)`, // Couleur de fond pour chaque joueur
            borderColor: `rgba(${index * 50}, ${index * 100}, ${index * 150}, 1)`, // Couleur de bordure pour chaque joueur
            hidden: !isCreator && player !== pseudo,
        })),
    }

    // Options de la chart
    const options = {
        responsive: true,
        plugins: {
            legend: {
              position: 'top' as const,
            },
            title: {
              display: true,
              text: cummulative ? 'Evolution du score total' : 'Evolution de la qualité des prédictions',
            },
        },
        scales: {
            x: {
              title: {
                display: true,
                text: 'Prédiction',
              },
            },
            y: {
              title: {
                display: true,
                text: 'Score',
              },
            }
        },
    }

    return (
        <>
            <FormControlLabel control={<Switch checked={cummulative} onChange={handleChange} inputProps={{ 'aria-label': 'controlled' }} />} label="Label" />
            <Line data={data} options={options} />
        </>
    )
  }
