import path from "path"
import fs from "fs"

export function getImagesInFolder(folder: string) {
    const build_path = path.join(__dirname, '..', 'build')
    const images_dir = path.join(build_path, 'images', folder)
    const images = fs.readdirSync(images_dir).filter(file => file.endsWith('.png')).map(file => path.join('images', folder, file))
    return images
  }
